import React from 'react';
import { CurrentUserContext} from '../contexts/CurrentUserContext.js';

function Card ({card, onCardClick, onCardLike, onCardDelete}) {
    const currentUser = React.useContext(CurrentUserContext);
    const isOwn = card.owner._id === currentUser._id;
    const isLiked = card.cardLikes.some(i => i._id === currentUser._id);
    // console.log(card);
    // Создаём переменную, которую после зададим в `className` для кнопки лайка
    const cardLikeButtonClassName = ( 
        `elements__like-icon ${isLiked && 'elements__like-icon_active'}` 
      );

    function handleClick() {
        onCardClick(card);
    }

    function handleCardLike() {
        onCardLike(card);
    }

    function handleDeleteClick(){
        onCardDelete(card.id);
    }

    return (
            <li className="elements__item" key = {card.id}>
                {isOwn && <button type="button" aria-label="Удалить" className='elements__trash-button' onClick={handleDeleteClick} />} 
                <div style={{ backgroundImage: `url(${card.cardLink})` }} className="elements__image" onClick={handleClick}></div>
                <div className="elements__item-description">
                    <h3 className="elements__title">{card.cardName}</h3>
                    <div className="elements__like-container">
                        <button type="button" aria-label="Лайк" className={cardLikeButtonClassName} onClick={handleCardLike}></button>
                        <p className="elements__like-count">{card.cardLikes.length}</p>
                    </div>
                </div>
            </li>
    );
}

export default Card;
