import React, { useState } from 'react';
import Header from './Header.js';
import Main from './Main.js';
import Footer from './Footer.js';
import ImagePopup from './ImagePopup.js';
import PopupWithForm from './PopupWithForm.js';

function App() {

    const [isEditAvatarPopupOpen, setEditAvatarPopupState] = React.useState(false);
    const [isEditProfilePopupOpen, setEditProfilePopupState] = React.useState(false);
    const [isAddPlacePopupOpen, setAddPlacePopupState] = React.useState(false);

    function closeAllPopups () {
        console.log(isEditAvatarPopupOpen);
        isEditAvatarPopupOpen && setEditAvatarPopupState(false);
        isEditProfilePopupOpen && setEditProfilePopupState(false);
        isAddPlacePopupOpen && setAddPlacePopupState(false);
        document.removeEventListener('keydown', handleEscClose);
    }

    function handleEscClose (event) {
        if(event.key === 'Escape') {
            closeAllPopups();
        }
    }

    function handleEditAvatarClick () {
        document.addEventListener('keydown', handleEscClose);
        setEditAvatarPopupState(true);
        // document.addEventListener('keydown', handleEscClose);
    }

    function handleEditProfileClick () {
        setEditProfilePopupState(true);
        document.addEventListener('keydown', handleEscClose);
    }

    function handleAddPlaceClick () {
        setAddPlacePopupState(true);
        document.addEventListener('keydown', handleEscClose);
    }

  return (
    <div className='page'>
        <Header />
        <Main onEditProfile={handleEditProfileClick} onAddPlace={handleAddPlaceClick} onEditAvatar={handleEditAvatarClick}/>
        <Footer />
        <PopupWithForm 
        title ='Обновить аватар' 
        name ='edit-avatar' 
        btnName = 'Сохранить'
        children = {
            <label class="popup__form-label">
                <input name="popupAvatar" type="url" placeholder="Ссылка на аватарку" id="avatar-input" class="popup__input popup__input_value_avatar" required />
                <span class="popup__input-error avatar-input-error"></span>
            </label>
        }
        isOpen={isEditAvatarPopupOpen} 
        onClose={closeAllPopups}  />

        <PopupWithForm 
        title='Редактировать профиль' 
        name='edit-profile' 
        btnName = 'Сохранить'
        children = {
            <>
                <label class="popup__form-label">
                    <input name="popupName" type="text" placeholder="Имя" id="name-input" class="popup__input popup__input_value_name" minlength="2" maxlength="40" required />
                    <span class="popup__input-error name-input-error"></span>
                </label>
                <label class="popup__form-label">
                    <input name="popupJob"  type="text" placeholder="Работа" id="job-input" class="popup__input popup__input_value_job" minlength="2" maxlength="200" required />
                    <span class="popup__input-error job-input-error"></span>
                </label>
            </>
        }
        isOpen={isEditProfilePopupOpen} 
        onClose={closeAllPopups}  />

        <PopupWithForm 
        title='Новое место' 
        name='add-card' 
        btnName = 'Создать'
        children = {
            <>
                <label class="popup__form-label">
                    <input name="popupName" type="text" placeholder="Название" id="place-input"  class="popup__input popup__input_value_name" required minlength="2" maxlength="30" />
                    <span class="popup__input-error place-input-error"></span>
                </label>
                <label class="popup__form-label">
                    <input name="popupImg"  type="url" placeholder="Ссылка на картинку" id="url-input" class="popup__input popup__input_value_img" required />
                    <span class="popup__input-error url-input-error"></span>
                </label>
            </>
        }
        isOpen={isAddPlacePopupOpen} 
        onClose={closeAllPopups} />

    {/* <div className="popup popup_type_edit-profile">
        <div className="popup__container">
            <button type="button" className="popup__close"></button>
            <div className="popup__content">
                <h2 className="popup__title">Редактировать профиль</h2>
                <form action="#" name="popupFormEditProfile" className="popup__form popup__form_type_edit-profile" novalidate>
                    <label className="popup__form-label">
                        <input name="popupName" type="text" placeholder="Имя" id="name-input" className="popup__input popup__input_value_name" minlength="2" maxlength="40" required />
                        <span className="popup__input-error name-input-error"></span>
                    </label>
                    <label className="popup__form-label">
                        <input name="popupJob"  type="text" placeholder="Работа" id="job-input" className="popup__input popup__input_value_job" minlength="2" maxlength="200" required />
                        <span className="popup__input-error job-input-error"></span>
                    </label>
                    <button type="submit" className="popup__save">Сохранить</button>
                </form>
            </div>
        </div>
    </div>
    <div className="popup popup_type_edit-avatar">
        <div className="popup__container">
            <button type="button" className="popup__close"></button>
            <div className="popup__content">
                <h2 className="popup__title">Обновить аватар</h2>
                <form action="#" name="popupFormEditAvatar" className="popup__form popup__form_type_edit-avatar" novalidate>
                    <label className="popup__form-label">
                        <input name="popupAvatar" type="url" placeholder="Ссылка на аватарку" id="avatar-input" className="popup__input popup__input_value_avatar" required />
                        <span className="popup__input-error avatar-input-error"></span>
                    </label>
                    <button type="submit" className="popup__save">Сохранить</button>
                </form>
            </div>
        </div>
    </div>
    <div className="popup popup_type_add-card">
        <div className="popup__container">
            <button type="button" className="popup__close"></button>
            <div className="popup__content">
                <h2 className="popup__title">Новое место</h2>
                <form action="#" name="popupFormAddCard" className="popup__form popup__form_type_add-card" novalidate>
                    <label className="popup__form-label">
                        <input name="popupName" type="text" placeholder="Название" id="place-input"  className="popup__input popup__input_value_name" required minlength="2" maxlength="30" />
                        <span className="popup__input-error place-input-error"></span>
                    </label>
                    <label className="popup__form-label">
                        <input name="popupImg"  type="url" placeholder="Ссылка на картинку" id="url-input" className="popup__input popup__input_value_img" required />
                        <span className="popup__input-error url-input-error"></span>
                    </label>
                    <button type="submit" className="popup__save">Создать</button>
                </form>
            </div>
        </div>
    </div> */}
    <div className="popup popup_type_confirmation">
        <div className="popup__container">
            <button type="button" className="popup__close"></button>
            <div className="popup__content">
                <h2 className="popup__title">Вы уверены?</h2>
                <form action="#" name="popupCardConfirm" className="popup__form popup__form_type_card-confirm">
                    <button type="submit" className="popup__save">Да</button>
                </form>

            </div>
        </div>
    </div>
    <ImagePopup />
    <template className="card-template">
        <li className="elements__item">
            <button type="button" aria-label="Удалить" className="elements__trash-button"></button>
            <img src="#" alt="" className="elements__image" />
            <div className="elements__item-description">
                <h3 className="elements__title"></h3>
                <div className="elements__like-container">
                    <button type="button" aria-label="Лайк" className="elements__like-icon"></button>
                    <p className="elements__like-count">5</p>
                </div>
            </div>
        </li>
    </template>
    </div>
  );
}

export default App;
