import React from "react";
import {Routes, Route, NavLink, useRoutes, matchRoutes} from "react-router-dom";
function PageWithForm(props) {
    const routeMatch = matchRoutes("/sign-up");
    return(
        <section className="entry">
            <div className="entry__content">
                <h1 className="entry__title">{props.title}</h1>
                <form className="entry__form">
                    {props.children}
                    <button type="submit" className="entry__button">{props.buttonName}</button>
                </form>
                //<NavLink to="/sign-in" className="header__link">Войти</NavLink>
                    {routeMatch&&<NavLink to="/sign-in" className="header__link">Войти</NavLink>}
            </div>
        </section>
    )
}
export default PageWithForm;
